from django import formsfrom .models import Employee, Department, Positionfrom django.contrib.auth.forms import AuthenticationForm, UserCreationFormclass LoginForm(AuthenticationForm):    username = forms.CharField(widget=forms.TextInput(attrs={        'class': 'form-control rounded-pill shadow-sm px-4',        'id': 'userName',        'placeholder': 'username',    }))    password = forms.CharField(widget=forms.PasswordInput(attrs={        'class': 'form-control rounded-pill shadow-sm px-4',        'placeholder': 'password',        'id': 'id_password',    }))class EmployeeCreationForm(forms.ModelForm):    first_name = forms.CharField(required=False, widget=forms.TextInput(attrs={        'class': 'form-control',        'id': 'fstName',        'placeholder': 'first name'    }))    last_name = forms.CharField(required=False, widget=forms.TextInput(attrs={        'class': 'form-control',        'id': 'lstName',        'placeholder': 'last name'    }))    username = forms.CharField(widget=forms.TextInput(attrs={        'class': 'form-control',        'id': 'id_username',        'placeholder': 'username'    }))    email = forms.EmailField(widget=forms.EmailInput(attrs={        'class': 'form-control',        'id': 'id_email',        'placeholder': 'email'    }), required=False)    dept = forms.ModelChoiceField(queryset=Department.objects.all(), empty_label='please select a department ',widget=forms.Select(attrs={        'class': 'form-control',        'id': 'id_dept',        'placeholder': 'department'    }))    position = forms.ModelChoiceField(queryset=Position.objects.all(), empty_label='please select a position ', widget=forms.Select(attrs={        'class': 'form-control',        'id': 'id_position',        'placeholder': 'position'    }))    salary = forms.CharField(empty_value="Enter basic salary", widget=forms.NumberInput(attrs={        'class': 'form-control',        'id': 'salary_id',        'placeholder': 'eg. 200000'    }))    age = forms.IntegerField( min_value=18, max_value=59, required=False, label='age', widget=forms.NumberInput(attrs={        'class': 'form-control',        'id': 'contact_id',        'placeholder': '0912345678',    }))    contact = forms.CharField(label='phone', required=False, widget=forms.TextInput(attrs={        'class': 'form-control',        'id': 'contact_id',        'placeholder': '0912345678',    }))    gender = forms.ChoiceField(choices=[('male', 'male'), ('female', 'female')], widget=forms.RadioSelect(attrs={        'class': 'form-check-input',    }))    password = forms.CharField(widget=forms.PasswordInput(attrs={        'class': 'form-control',        'id': 'id_password',        'placeholder': 'password'    }))    class Meta:        model = Employee        fields = ['first_name', 'last_name', 'username', 'email', 'dept', 'position', 'password']        # exclude = ['']class LeaveForm(forms.ModelForm):    ...