from django import formsfrom .models import Productfrom django.utils.translation import gettext_lazy as _from .models import *class ProductCreationForm(forms.ModelForm):    code = forms.CharField(label=_('Product Code'), widget=forms.TextInput(attrs={        'class': 'form-control',        'id': 'productCode',        'placeholder': 'eg. PDS-10',    }))    name = forms.CharField(label=_('Product Name'), widget=forms.TextInput(attrs={        'class': 'form-control',        'id': 'productName',        'placeholder': 'eg. Kanote Powerdome 10k 22.5W ...'    }))    brand = forms.ModelChoiceField(Brand.objects.all(), empty_label='select a brand', label=_('Product brand'), widget=forms.Select(attrs={        'class': 'form-control',        'id': 'productBrand'    }))    price = forms.IntegerField(label=_('Product Price'), min_value=0, widget=forms.NumberInput(attrs={        'class': 'form-control',        'id': 'productPrice',        'placeholder': 'eg. 10000'    }))    warranty = forms.ChoiceField(label=_('Product Warranty'), initial=3, choices=Product.WARRANTY_MONTHS, widget=forms.Select(attrs={        'class': 'form-control',    }))    class Meta:        model = Product        fields = '__all__'        exclude = ['slug']class BrandCreationForm(forms.ModelForm):    class Meta:        model = Brand        fields = ['name',]