from django.urls import include, pathfrom .views import *app_name = 'productions'urlpatterns = [    path('creation/', ProductCreationView.as_view(), name='create_product'),    path('all_+products/', ProductListView.as_view(), name='product-list'),    path('look-detail/?product-no=<str:code>/', ProductDetailView.as_view(), name='product-detail'),    path('delete/?product-no=<str:code>/', product_delete, name='delete-product'),]