import pandas as pdfrom .models import ErrorReturnfrom django.http import JsonResponsedef monthly_reporting_chart(request):    error = ErrorReturn.objects.all().values()    df = pd.DataFrame.from_records(error)    label = list(set(df['product_id']))    counting = df['product_id'].value_counts()    label = counting.index.tolist()    data_values = counting.values.tolist()    data = {        'labels': label,        'count': data_values,        'cols': len(data_values)    }    return JsonResponse(data)