from django import templatefrom datetime import datetime, timedelta, timezonefrom dateutil import relativedeltafrom django.utils.timesince import timesincefrom ..models import ErrorReturnregister = template.Library()@register.filterdef uncheck():    object_list = ErrorReturn.objects.filter(status='uncheck')    return object_list@register.filterdef late(meanwhile=3):    object_list = []    for error in ErrorReturn.objects.order_by('-received_at'):        if not error.servicing.checked:            time_difference = error.received_at - datetime.today()            if time_difference >= timedelta(days=meanwhile):                object_list.append(error)    return object_list@register.filterdef done():    object_list = []    for error in ErrorReturn.objects.all():        if error.servicing.done:            object_list.append(error)    return object_list